function setArtlist() {
	if ($("section.artlist").size() == 0) return;
	var a = $("header").outerWidth();
	var b = $(".toolbar .switch");
	if (b.css("display") == "none" || b.hasClass("glyphicon-th-list")) a = 0;
	var c = $(".artlist ul.items li p.left").outerWidth();
	var d = $(".artlist ul.items").css("margin-left").replace("px", "");
	var e = 100;
	if ($(".artlist ul.items li p.left").css("display") == "none") e = 0;
	else if ($(".dock").css("display") == "none") e = 30;
	$(".artlist ul.items li p.right").width($(window).width() - a - d - c - e)
}
function setArtshow() {
	if ($("section.artshow").size() == 0) return;
	var a = $("header").outerWidth();
	var b = $(".toolbar .switch");
	if (b.css("display") == "none" || b.hasClass("glyphicon-th-list")) a = 0;
	var c = 0;
	var d = $(".artshow .box").css("margin-left").replace("px", "");
	if ($(".artshow aside").width() > 0) {
		c = 75;
		d = d * 2
	}
	$(".artshow .box").width($(window).width() - a - d - $(".artshow aside").outerWidth() - c);
	var e = parseInt(($(window).height() - $(".artshow aside").outerHeight()) / 2);
	$(".artshow aside").css("top", e).addClass("active")
}
function setCaselist() {
	if ($("section.caselist").size() == 0) return;
	var a = $("header").outerWidth();
	var b = $(".toolbar .switch");
	if (b.css("display") == "none" || b.hasClass("glyphicon-th-list")) a = 0;
	$("section.caselist").width($(window).width() - a);
	var c = parseInt($(window).width() / $(".caselist ul.items li").width());
	var d = parseInt($(".caselist ul.items li").css("margin-left").replace("px", "")) + 2;
	var w = $(window).width() - $(".caselist ul.items li").width() * c - d * c * 2 + 5;
	$(".caselist ul.items").css("margin-left", parseInt(w / 2))
}
function setCaseshow() {
	if ($("section.caseshow").size() == 0) return;
	var a = $("header").outerWidth();
	var b = $(".toolbar .switch");
	if (b.css("display") == "none" || b.hasClass("glyphicon-th-list")) a = 0;
	var w = $(window).width() - a;
	var c = 200;
	if ($(".dock").css("display") == "none") c = 100;
	$(".caseshow").width(w);
	if (w - c < 1000) $(".caseshow .demo").width(w - c).find("img").addClass("img-responsive")
}
function dockEvent() {
	$(".dock").height($(".dock ul.icons li").length * 55 - 5);
	$(".dock ul.icons li i").bind("touchstart mouseenter click", function() {
		$(".dock ul.icons li").removeClass("active");
		$(this).parent().addClass("active")
	});
	$(".dock ul.icons li").bind("mouseleave", function() {
		$(".dock ul.icons li").removeClass("active")
	});
	$(".dock ul.icons li.up i").bind("touchstart click", function() {
		$("html,body").animate({
			scrollTop: 0
		}, 500)
	})
}